div.script:
$(function () {
    $('.choose_all').on('click', function () {
        var checkboxs = $('[name="orgs"]').find('[type="checkbox"]');
        if ($(this).text() == '全选') {
            checkboxs.each(function () {
                $(this).prop('checked', true);
                $(this).trigger('change');
            });
            $(this).text("取消");
        } else {
            checkboxs.each(function () {
                $(this).prop('checked', false);

            });
            checkboxs.trigger('change');
            $(this).text("全选");
        }
    });
});
